import { Database } from '@nozbe/watermelondb';
import LokiJSAdapter from '@nozbe/watermelondb/adapters/lokijs';

import Business from './models/business';
import Category from './models/categories';
import Customer from './models/customers';
import Inventory from './models/inventory';
import InventoryBatch from './models/inventory_batches';
import Product from './models/products';
import Role from './models/roles';
import Sale from './models/sales';
import SaleItem from './models/sales_items';
import Session from './models/sessions';
import Staff from './models/staff';
import Store from './models/stores';
import User from './models/users';
import schema from './schema';

export {
    Business,
    Category,
    Customer,
    Inventory,
    InventoryBatch,
    Product,
    Role,
    Sale,
    SaleItem,
    Session,
    Staff,
    Store,
    User,
};

// First, create the adapter to the underlying database:
const adapter = new LokiJSAdapter({
    schema,
    useWebWorker: false,
    useIncrementalIndexedDB: true,
    dbName: 'pos',
    onSetUpError: (error) => {
        console.error('Database setup failed', error);
    },
});

// Then, make a Watermelon database from it
export const database = new Database({
    adapter,
    modelClasses: [
        User,
        Session,
        Business,
        Store,
        Role,
        Staff,
        Category,
        Product,
        Inventory,
        InventoryBatch,
        Customer,
        Sale,
        SaleItem,
    ],
    // actionsEnabled: true,
});

export const storeCollection = database.collections.get<Store>('stores');
export const userCollection = database.collections.get<User>('users');
export const sessionCollection = database.collections.get<Session>('sessions');
export const categoriesCollection = database.collections.get<Category>('categories');
export const salesCollection = database.collections.get<Sale>('sales');
export const saleItemsCollection = database.collections.get<SaleItem>('sales_items');
export const customersCollection = database.collections.get<Customer>('customers');
export const productsCollection = database.collections.get<Product>('products');
export const inventoryCollection = database.collections.get<Inventory>('inventory');
export const inventoryBatchesCollection =
    database.collections.get<InventoryBatch>('inventory_batches');
export const staffCollection = database.collections.get<Staff>('staff');
export const businessCollection = database.collections.get<Business>('businesses');
export const roleCollection = database.collections.get<Role>('roles');

components/common/QuantityControls.tsx.content:
import React from 'react';
import { Text, View } from 'react-native';
import { Minus, Plus } from 'lucide-react-native';
import { ActionButton } from '../ui/ActionButton';

export const QuantityControls: React.FC<{
  quantity: number;
  onIncrease: () => void;
  onDecrease: () => void;
  size?: 'xs' | 'sm' | 'md' | 'lg';
  variant?: 'primary' | 'secondary';
}> = ({
  quantity,
  onIncrease,
  onDecrease,
  size = 'sm',
  variant = 'primary'
}) => {
  const textSizeClass = {
    xs: 'text-xs',
    sm: 'text-sm', 
    md: 'text-base',
    lg: 'text-lg'
  }[size];

  const quantityWidthClass = {
    xs: 'w-8',
    sm: 'w-10',
    md: 'w-12',
    lg: 'w-16'
  }[size];

  const heightClass = {
    xs: 'h-6',
    sm: 'h-8',
    md: 'h-10',
    lg: 'h-12'
  }[size];

  // Border radius based on size - more subtle rounding
  const borderRadiusClass = {
    xs: 'rounded-md', // 6px
    sm: 'rounded-lg', // 8px
    md: 'rounded-xl', // 12px
    lg: 'rounded-2xl' // 16px
  }[size];

  // Background colors for the middle section based on variant
  const quantityBgClass = variant === 'primary' ? 'bg-gray-900' : 'bg-white';
  const quantityTextClass = variant === 'primary' ? 'text-white' : 'text-gray-700';

  return (
    <View className={`flex-row items-center ${borderRadiusClass} overflow-hidden`}>
      {/* Decrease Button - Left side rounded */}
      <ActionButton
        icon={Minus}
        iconPosition="only"
        onPress={onDecrease}
        variant={variant}
        size={size}
        shape="rectangle"
        disabled={quantity <= 0}
        className="rounded-none" // Remove all rounding, let container handle it
      />

      {/* Quantity Display - Middle section */}
      <View 
        className={`${heightClass} ${quantityWidthClass} ${quantityBgClass} items-center justify-center`}
        style={{
          borderLeftWidth: 1,
          borderRightWidth: 1,
          borderColor: '#696a6bff' // gray-200
        }}
      >
        <Text className={`${textSizeClass} font-medium ${quantityTextClass}`}>
          {quantity}
        </Text>
      </View>

      {/* Increase Button - Right side rounded */}
      <ActionButton
        icon={Plus}
        iconPosition="only"
        onPress={onIncrease}
        variant={variant}
        size={size}
        shape="rectangle"
        className="rounded-none" 
      />
    </View>
  );
};

