import React, { useState, useEffect } from 'react';
import {
	Modal,
	View,
	Text,
	TextInput,
	TouchableOpacity,
	KeyboardAvoidingView,
	Platform,
	ScrollView,
} from 'react-native';
import { X, Trash2 } from 'lucide-react-native';
import { ActionButton } from '@/components/ui/ActionButton';
import { Category, Product } from '@/types';

interface EditInventoryModalProps {
	visible: boolean;
	item: Product | null;
	onClose: () => void;
	onSave: (item: Product) => void;
	onDelete: (itemId: string) => void;
	availableCategories: Category[];
}

export const EditInventoryModal: React.FC<EditInventoryModalProps> = ({
	visible,
	item,
	onClose,
	onSave,
	onDelete,
	availableCategories,
}) => {
	const [formData, setFormData] = useState<Product>({
		id: '',
		name: '',
		brand: '',
		category: '',
		size: '',
		unit: '',
		stock: 0,
		minimumAlert: 0,
		purchasePrice: 0,
		price: 0,
	});

	const [isSubmitting, setIsSubmitting] = useState(false);

	useEffect(() => {
		if (item) {
			setFormData(item);
		}
	}, [item]);

	const resetForm = () => {
		setFormData({
			id: '',
			name: '',
			brand: '',
			category: '',
			size: '',
			unit: '',
			stock: 0,
			minimumAlert: 0,
			purchasePrice: 0,
			price: 0,
		});
		setIsSubmitting(false);
	};

	const handleClose = () => {
		resetForm();
		onClose();
	};

	const handleSave = async () => {
		if (!formData.name.trim() || !formData.category.trim()) {
			return;
		}

		setIsSubmitting(true);

		try {
			onSave(formData);
			resetForm();
			onClose();
		} catch (error) {
			console.error('Error saving item:', error);
		} finally {
			setIsSubmitting(false);
		}
	};

	const handleDelete = () => {
		if (item?.id) {
			onDelete(item.id);
			handleClose();
		}
	};

	const updateField = (field: keyof Product, value: any) => {
		setFormData((prev) => ({ ...prev, [field]: value }));
	};

	const isFormValid = formData.name.trim() && formData.category.trim();

	return (
		<Modal visible={visible} transparent animationType='fade' onRequestClose={handleClose}>
			<View className='flex-1 bg-black/50 justify-center items-center p-4'>
				<KeyboardAvoidingView
					behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
					className='w-full max-w-md'>
					<View className='bg-white rounded-2xl p-6 w-full'>
						{/* Header */}
						<View className='flex-row items-center justify-between mb-2'>
							<Text className='text-lg font-semibold text-gray-900'>
								Edit Inventory Item
							</Text>
							<TouchableOpacity
								onPress={handleClose}
								className='p-1'
								hitSlop={{ top: 8, bottom: 8, left: 8, right: 8 }}>
								<X size={20} color='#6B7280' />
							</TouchableOpacity>
						</View>

						<Text className='text-sm text-gray-500 mb-4'>
							Update the details of this inventory item.
						</Text>

						<ScrollView showsVerticalScrollIndicator={false} className='max-h-96'>
							{/* Product Name */}
							<View className='mb-4'>
								<Text className='text-sm font-medium text-gray-900 mb-2'>
									Product Name <Text className='text-red-500'>*</Text>
								</Text>
								<TextInput
									value={formData.name}
									onChangeText={(text) => updateField('name', text)}
									placeholder='Enter product name'
									placeholderTextColor='#9CA3AF'
									className='bg-gray-50 rounded-lg px-3 py-3 text-gray-900 border border-gray-200'
									autoCapitalize='words'
								/>
							</View>

							{/* Brand */}
							<View className='mb-4'>
								<Text className='text-sm font-medium text-gray-900 mb-2'>
									Brand
								</Text>
								<TextInput
									value={formData.brand}
									onChangeText={(text) => updateField('brand', text)}
									placeholder='Enter brand'
									placeholderTextColor='#9CA3AF'
									className='bg-gray-50 rounded-lg px-3 py-3 text-gray-900 border border-gray-200'
									autoCapitalize='words'
								/>
							</View>

							{/* Category */}
							<View className='mb-4'>
