hooks/useStoreEdit.ts.content:
import { StoreData } from '@/types';
import { useCallback, useEffect, useState } from 'react';

interface EditableStoreData {
    name: string;
    type: string;
    phone: string;
    email: string;
    address: string;
    description: string;
    weekdayHours: string;
    weekendHours: string;
}

export const useStoreEdit = (initialData: StoreData, externalId?: string) => {
    const [isEditing, setIsEditing] = useState(false);
    const [storeData, setStoreData] = useState<StoreData>(initialData);
    const [editedData, setEditedData] = useState<EditableStoreData>({
        name: initialData.name,
        type: initialData.type,
        phone: initialData.phone,
        email: initialData.email,
        address: initialData.address,
        description: initialData.description,
        weekdayHours: initialData.businessHours?.weekdays ?? '',
        weekendHours: initialData.businessHours?.weekend ?? '',
    });

    // Reset edited data when initial data changes
    useEffect(() => {
        setStoreData(initialData);
        setEditedData({
            name: initialData.name,
            type: initialData.type,
            phone: initialData.phone,
            email: initialData.email,
            address: initialData.address,
            description: initialData.description,
            weekdayHours: initialData.businessHours?.weekdays ?? '',
            weekendHours: initialData.businessHours?.weekend ?? '',
        });
    }, [initialData]);

    const handleEdit = useCallback(() => {
        setIsEditing(true);
    }, []);

    const handleCancel = useCallback(() => {
        setIsEditing(false);
        setEditedData({
            name: storeData.name,
            type: storeData.type,
            phone: storeData.phone,
            email: storeData.email,
            address: storeData.address,
            description: storeData.description,
            weekdayHours: storeData.businessHours?.weekdays ?? '',
            weekendHours: storeData.businessHours?.weekend ?? '',
        });
    }, [storeData]);

    const handleSaveChanges = useCallback(async () => {
        console.log(' Saving changes for externalId:', externalId, editedData);
        try {
            // TODO: Update store in database using externalId
            // await updateStoreById(externalId, editedData);

            // Update local state
            setStoreData({
                ...storeData,
                name: editedData.name,
                type: editedData.type,
                phone: editedData.phone,
                email: editedData.email,
                address: editedData.address,
                description: editedData.description,
                businessHours: {
                    weekdays: editedData.weekdayHours,
                    weekend: editedData.weekendHours,
                },
            });
            setIsEditing(false);
            console.log(' Changes saved successfully');
            return true;
        } catch (error) {
            console.error(' Error saving changes:', error);
            return false;
        }
    }, [externalId, editedData, storeData]);

    const updateField = useCallback((field: keyof EditableStoreData, value: string) => {
        setEditedData((prev) => ({ ...prev, [field]: value }));
    }, []);

    return {
        isEditing,
        storeData,
        editedData,
        setIsEditing,
        handleEdit,
        handleCancel,
        handleSaveChanges,
        updateField,
    };
};

hooks/useProducts.ts.content:
import { useState, useCallback, useMemo } from 'react';
import { Product, CartItem } from '@/types';
import { SAMPLE_PRODUCTS } from '@/constants/sampleProducts';

export const useProducts = (
	categoryName: string,
	existingCart: CartItem[] = [],
	onCartUpdate?: (cartItems: CartItem[]) => void,
) => {
	// Initialize products with existing cart quantities
	const initializeProductsWithCart = useCallback(
		(products: Product[], cart: CartItem[]): Product[] => {
			return products.map((product) => {
				const cartItem = cart.find((item) => item.id === product.id);
				return {
					...product,
					inCart: cartItem ? cartItem.quantity : 0,
				};
			});
		},
		[],
	);

	const categoryProducts = useMemo(
		() => SAMPLE_PRODUCTS.filter((product) => product.category === categoryName),
		[categoryName],
	);

	const [productList, setProductList] = useState<Product[]>(() =>
		initializeProductsWithCart(categoryProducts, existingCart),
	);

	const updateProductQuantity = useCallback(
		(productId: string, newQuantity: number) => {
			setProductList((prev) => {
				const updatedProducts = prev.map((product) =>
					product.id === productId
						? { ...product, inCart: Math.max(0, newQuantity) }
						: product,
				);

				// Update global cart when local cart changes
				if (onCartUpdate) {
					const cartItems = updatedProducts
						.filter((p) => (p.inCart || 0) > 0)
						.map((p) => ({
							id: p.id,
							name: p.name,
							price: p.price,
							quantity: p.inCart || 0,
						}));
					onCartUpdate(cartItems);
				}

				return updatedProducts;
			});
		},
		[onCartUpdate],
	);

	const addToCart = useCallback(
		(product: Product) => {
			updateProductQuantity(product.id, (product.inCart || 0) + 1);
		},
		[updateProductQuantity],
	);

	const removeFromCart = useCallback(
		(product: Product) => {
			updateProductQuantity(product.id, (product.inCart || 0) - 1);
		},
		[updateProductQuantity],
	);

	const filteredProducts = useMemo(
		() => (searchQuery: string) =>
			productList.filter(
				(product) =>
					product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
					product.brand?.toLowerCase().includes(searchQuery.toLowerCase()),
			),
		[productList],
	);

	const cartTotals = useMemo(() => {
		const totalItems = productList.reduce((sum, product) => sum + (product.inCart || 0), 0);
		const totalPrice = productList.reduce(
			(sum, product) => sum + product.price * (product.inCart || 0),
			0,
		);
		return { totalItems, totalPrice };
	}, [productList]);

	const getCartItems = useCallback(
		() => productList.filter((p) => (p.inCart || 0) > 0),
		[productList],
	);

	return {
		productList,
		addToCart,
		removeFromCart,
		filteredProducts,
		cartTotals,
		getCartItems,
	};
};

