                placeholder={placeholder}
                value={value}
                onChangeText={onChangeText}
                keyboardType={keyboardType}
                placeholderTextColor='#999'
                multiline={multiline}
                numberOfLines={numberOfLines}
                textAlignVertical={textAlignVertical}
