	selectedMethod,
	onSelectMethod,
}) => {
	const methods: { id: PaymentMethodType; label: string; icon: any }[] = [
		{ id: 'Cash', label: 'Cash', icon: DollarSign },
		{ id: 'M-Pesa', label: 'M-Pesa', icon: Smartphone },
