components/inventory/AddItemModal.tsx.content:
import { Ionicons } from "@expo/vector-icons";
import FormInput from "./FormInput";
import React, { useState } from "react";
import { Modal, ScrollView, Text, TouchableOpacity, View } from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";
import FormDropdown from "./FormDropdown";
import FormToggle from "./FormToggle";


interface AddItemModalProps {
  visible: boolean;
  onClose: () => void;
  onAddItem: (item: any) => void;
}

const AddItemModal = ({ visible, onClose, onAddItem }: AddItemModalProps) => {
  const [formData, setFormData] = useState({
    productName: "",
    brand: "",
    category: "",
    sizeQuantity: "",
    unit: "",
    stock: "0",
    minimumAmount: "0",
    purchasePrice: "0",
    sellPrice: "0",
    itemOrdered: false,
  });

  const categories = [
    "Beverages",
    "Snacks",
    "Dairy",
    "Bakery",
    "Fruits",
    "Vegetables",
    "Meat",
    "Frozen",
    "Household",
    "Personal Care",
  ];

  const units = [
    "kg",
    "g",
    "liter",
    "ml",
    "piece",
    "packet",
    "bottle",
    "loaf",
    "can",
  ];

  const handleInputChange = (field: string, value: any) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleAddItem = () => {
    onAddItem(formData);
    setFormData({
      productName: "",
      brand: "",
      category: "",
      sizeQuantity: "",
      unit: "",
      stock: "0",
      minimumAmount: "0",
      purchasePrice: "0",
      sellPrice: "0",
      itemOrdered: false,
    });
  };

  const isFormValid = formData.productName && formData.category;

  return (
    <Modal
      visible={visible}
      animationType="slide"
      presentationStyle="pageSheet"
    >
      <SafeAreaView className="flex-1 bg-white">
        <View className="flex-row justify-between items-center px-4 py-4 border-b border-gray-200">
          <Text className="text-xl font-bold text-black">
            Add New Inventory Item
          </Text>
          <TouchableOpacity onPress={onClose}>
            <Ionicons name="close" size={24} color="#666" />
          </TouchableOpacity>
        </View>
        <ScrollView
          className="flex-1 px-4 py-6"
          showsVerticalScrollIndicator={false}
        >
          <Text className="text-gray-600 text-center mb-6">
            Create a new product in your inventory with all necessary details.
          </Text>
          <View className="space-y-4">
            <FormInput
              label="Product Name"
              required
              placeholder="Enter product name"
              value={formData.productName}
              onChangeText={(value) => handleInputChange("productName", value)}
            />
            <FormInput
              label="Brand"
              placeholder="Enter brand name"
              value={formData.brand}
              onChangeText={(value) => handleInputChange("brand", value)}
            />
            <View className="flex-row items-center space-x-3">
              <View className="flex-1">
                <FormDropdown
                  label="Category"
                  required
                  placeholder="Select category"
                  value={formData.category}
                  options={categories}
                  onSelect={(value) => handleInputChange("category", value)}
                />
              </View>
              <TouchableOpacity className="w-12 h-12 border-2 border-gray-300 rounded-lg justify-center items-center mt-6 mx-2">
                <Ionicons name="add" size={20} color="#666" />
              </TouchableOpacity>
            </View>
            <View className="flex-row space-x-3 ">
              <View className="flex-1 mx-2">
                <FormInput
                  label="Size/Quantity"
                  placeholder="e.g., 500ml"
                  value={formData.sizeQuantity}
                  onChangeText={(value) =>
                    handleInputChange("sizeQuantity", value)
                  }
                />
              </View>
              <View className="flex-1 mx-2">
                <FormDropdown
                  label="Unit"
                  placeholder="Select unit"
                  value={formData.unit}
                  options={units}
                  onSelect={(value) => handleInputChange("unit", value)}
                />
              </View>
            </View>
            <View className="flex-row space-x-3">
              <View className="flex-1 mx-2">
                <FormInput
                  label="Stock"
                  placeholder="0"
                  value={formData.stock}
                  onChangeText={(value) => handleInputChange("stock", value)}
                  keyboardType="numeric"
                />
              </View>
              <View className="flex-1 mx-2">
                <FormInput
                  label="Minimum Amount"
                  placeholder="0"
                  value={formData.minimumAmount}
                  onChangeText={(value) =>
                    handleInputChange("minimumAmount", value)
                  }
                  keyboardType="numeric"
                />
              </View>
            </View>
            <View className="flex-row space-x-3">
              <View className="flex-1 mx-2">
                <FormInput
                  label="Purchase Price ($)"
                  placeholder="0"
                  value={formData.purchasePrice}
                  onChangeText={(value) =>
                    handleInputChange("purchasePrice", value)
                  }
                  keyboardType="numeric"
                />
              </View>
              <View className="flex-1 mx-2">
                <FormInput
                  label="Sell Price ($)"
                  placeholder="0"
                  value={formData.sellPrice}
                  onChangeText={(value) =>
                    handleInputChange("sellPrice", value)
                  }
                  keyboardType="numeric"
                />
              </View>
            </View>
            <FormToggle
              label="Item Ordered"
              description="Mark if this item has been ordered from supplier"
              value={formData.itemOrdered}
              onToggle={(value) => handleInputChange("itemOrdered", value)}
            />
          </View>
        </ScrollView>
        <View className="flex-row px-4 py-4  space-x-3 border-t border-gray-200">
          <TouchableOpacity
            className="flex-1 bg-gray-200 rounded-xl py-4 mx-2"
            onPress={onClose}
          >
            <Text className="text-center font-semibold text-gray-700">
              Cancel
            </Text>
          </TouchableOpacity>
          <TouchableOpacity
            className={`flex-1 rounded-xl py-4 ${
              isFormValid ? "bg-black" : "bg-gray-300"
            } mx-2`}
            onPress={handleAddItem}
            disabled={!isFormValid}
          >
            <Text
              className={`text-center font-semibold ${
                isFormValid ? "text-white" : "text-gray-500"
              }`}
            >
              Add Item
            </Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    </Modal>
  );
};

export default AddItemModal;

