import React, { useState, useEffect } from 'react';
import {
    View,
    Text,
    TextInput,
    TouchableOpacity,
    ScrollView,
    Alert,
    ActivityIndicator,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { router, useLocalSearchParams } from 'expo-router';
import { useBusiness } from '@/contexts/BusinessContext';
import { useAuth } from '@/contexts/AuthContext';
import { inventoryService } from '@/db/services/inventoryService';

export default function AdjustInventoryScreen() {
    const { inventoryId } = useLocalSearchParams<{ inventoryId: string }>();
    const { selectedStore } = useBusiness();
    const { user } = useAuth();
    const [loading, setLoading] = useState(true);
    const [inventory, setInventory] = useState<any>(null);
    const [product, setProduct] = useState<any>(null);
    const [adjustmentType, setAdjustmentType] = useState<'add' | 'remove'>('add');
    const [quantity, setQuantity] = useState('');
    const [costPerUnit, setCostPerUnit] = useState('');
    const [notes, setNotes] = useState('');
    const [batchType, setBatchType] = useState<'purchase' | 'adjustment' | 'return' | 'damage'>('purchase');

    useEffect(() => {
        loadInventory();
    }, []);

    const loadInventory = async () => {
        if (!inventoryId) return;

        try {
            const data = await inventoryService.getInventoryWithProduct(inventoryId);
            setInventory(data.inventory);
            setProduct(data.product);
            setCostPerUnit(data.inventory.lastPurchasePrice?.toString() || '');
        } catch (error) {
            console.error('Error loading inventory:', error);
            Alert.alert('Error', 'Failed to load inventory');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async () => {
        if (!quantity || parseFloat(quantity) <= 0) {
            Alert.alert('Error', 'Please enter a valid quantity');
            return;
        }

        if (!user) {
            Alert.alert('Error', 'User not found');
            return;
        }

        setLoading(true);

        try {
            const quantityChange = adjustmentType === 'add' 
                ? parseFloat(quantity) 
                : -parseFloat(quantity);

            await inventoryService.adjustInventoryWithBatch(
                inventoryId,
                quantityChange,
                {
                    userId: user.id,
                    costPerUnit: parseFloat(costPerUnit) || 0,
                    batchType,
                    notes,
                }
            );

            Alert.alert('Success', 'Inventory adjusted successfully!', [
                {
                    text: 'OK',
                    onPress: () => router.back(),
                },
            ]);
        } catch (error) {
            console.error('Error adjusting inventory:', error);
            Alert.alert('Error', 'Failed to adjust inventory');
        } finally {
            setLoading(false);
        }
    };

    if (loading && !inventory) {
        return (
            <SafeAreaView className="flex-1 bg-white items-center justify-center">
                <ActivityIndicator size="large" color="#3B82F6" />
            </SafeAreaView>
        );
    }

    return (
        <SafeAreaView className="flex-1 bg-white">
            <View className="px-4 py-3 border-b border-gray-200">
                <View className="flex-row items-center justify-between">
                    <TouchableOpacity onPress={() => router.back()}>
                        <Text className="text-blue-600 text-base">Cancel</Text>
                    </TouchableOpacity>
                    <Text className="text-lg font-semibold">Adjust Inventory</Text>
                    <TouchableOpacity onPress={handleSubmit} disabled={loading}>
                        {loading ? (
                            <ActivityIndicator size="small" color="#3B82F6" />
                        ) : (
                            <Text className="text-blue-600 text-base font-semibold">Save</Text>
                        )}
                    </TouchableOpacity>
                </View>
            </View>

            <ScrollView className="flex-1 px-4 py-4">
                {product && (
                    <View className="mb-6 p-4 bg-gray-50 rounded-lg">
                        <Text className="text-lg font-semibold mb-1">{product.name}</Text>
                        <Text className="text-sm text-gray-600">
                            Current Stock: {inventory?.quantity || 0} {product.unit}
                        </Text>
                        <Text className="text-sm text-gray-600">
                            Price: ${inventory?.price?.toFixed(2) || '0.00'}
                        </Text>
                    </View>
                )}

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-2">Adjustment Type</Text>
                    <View className="flex-row gap-3">
                        <TouchableOpacity
                            className={`flex-1 py-3 rounded-lg ${
                                adjustmentType === 'add' ? 'bg-green-600' : 'bg-gray-200'
                            }`}
                            onPress={() => setAdjustmentType('add')}
                        >
                            <Text className={`text-center font-semibold ${
                                adjustmentType === 'add' ? 'text-white' : 'text-gray-700'
                            }`}>
                                Add Stock
                            </Text>
                        </TouchableOpacity>
                        <TouchableOpacity
                            className={`flex-1 py-3 rounded-lg ${
                                adjustmentType === 'remove' ? 'bg-red-600' : 'bg-gray-200'
                            }`}
                            onPress={() => setAdjustmentType('remove')}
                        >
                            <Text className={`text-center font-semibold ${
                                adjustmentType === 'remove' ? 'text-white' : 'text-gray-700'
                            }`}>
                                Remove Stock
                            </Text>
                        </TouchableOpacity>
                    </View>
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-2">Reason</Text>
                    <View className="flex-row flex-wrap gap-2">
                        {['purchase', 'adjustment', 'return', 'damage'].map((type) => (
                            <TouchableOpacity
                                key={type}
                                className={`px-4 py-2 rounded-full ${
                                    batchType === type ? 'bg-blue-600' : 'bg-gray-200'
                                }`}
                                onPress={() => setBatchType(type as any)}
                            >
                                <Text className={`capitalize ${
                                    batchType === type ? 'text-white' : 'text-gray-700'
                                }`}>
                                    {type}
                                </Text>
                            </TouchableOpacity>
                        ))}
                    </View>
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Quantity *</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={quantity}
                        onChangeText={setQuantity}
                        placeholder="Enter quantity"
                        keyboardType="numeric"
                    />
                </View>

                {(batchType === 'purchase' || adjustmentType === 'add') && (
                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Cost Per Unit</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2"
                            value={costPerUnit}
                            onChangeText={setCostPerUnit}
                            placeholder="0.00"
                            keyboardType="decimal-pad"
                        />
                    </View>
                )}

                <View className="mb-6">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Notes</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={notes}
                        onChangeText={setNotes}
                        placeholder="Add notes (optional)"
                        multiline
                        numberOfLines={3}
                        textAlignVertical="top"
                    />
                </View>

                {quantity && parseFloat(quantity) > 0 && (
                    <View className="p-4 bg-blue-50 rounded-lg">
                        <Text className="text-sm text-gray-700">
                            New Stock Level: {' '}
                            <Text className="font-semibold">
                                {adjustmentType === 'add'
                                    ? (inventory?.quantity || 0) + parseFloat(quantity)
                                    : (inventory?.quantity || 0) - parseFloat(quantity)
                                }{' '}
                                {product?.unit}
                            </Text>
                        </Text>
                    </View>
                )}
            </ScrollView>
        </SafeAreaView>
    );
}

