import { Q } from '@nozbe/watermelondb';
import { customersCollection, database } from '..';

export const customerService = {
    async createCustomer(data: {
        businessId: string;
        name: string;
        phone?: string;
        email?: string;
        creditLimit?: number;
    }) {
        return await database.write(async () => {
            return await customersCollection.create((customer) => {
                customer.externalId = `cust_${data.name}_${Date.now()}`;
                customer.businessId = data.businessId;
                customer.name = data.name;
                customer.phone = data.phone || '';
                customer.email = data.email || '';
                customer.creditLimit = data.creditLimit || 0;
                customer.currentBalance = 0;
                customer.reputationScore = 100;
                customer.deleted = false;
            });
        });
    },

    async updateCustomer(
        customerId: string,
        updates: Partial<{
            name: string;
            phone: string;
            email: string;
            creditLimit: number;
            reputationScore: number;
        }>,
    ) {
        const customer = await customersCollection.find(customerId);

        return await database.write(async () => {
            return await customer.update((c) => {
                if (updates.name !== undefined) c.name = updates.name;
                if (updates.phone !== undefined) c.phone = updates.phone;
                if (updates.email !== undefined) c.email = updates.email;
                if (updates.creditLimit !== undefined) c.creditLimit = updates.creditLimit;
                if (updates.reputationScore !== undefined)
                    c.reputationScore = updates.reputationScore;
            });
        });
    },

    async recordPayment(customerId: string, amount: number) {
        const customer = await customersCollection.find(customerId);

        return await database.write(async () => {
            return await customer.update((c) => {
                c.currentBalance = Math.max(0, (c.currentBalance || 0) - amount);
            });
        });
    },

    async deleteCustomer(customerId: string) {
        const customer = await customersCollection.find(customerId);

        return await database.write(async () => {
            return await customer.update((c) => {
                c.deleted = true;
            });
        });
    },

    async getCustomerById(customerId: string) {
        return await customersCollection.find(customerId);
    },

    async getCustomersByBusiness(businessId: string) {
        return await customersCollection
            .query(
                Q.where('business_id', businessId),
                Q.where('deleted', false),
                Q.sortBy('name', Q.asc),
            )
            .fetch();
    },

    async searchCustomers(businessId: string, searchTerm: string) {
        return await customersCollection
            .query(
                Q.where('business_id', businessId),
                Q.where('deleted', false),
                Q.or(
                    Q.where('name', Q.like(`%${Q.sanitizeLikeString(searchTerm)}%`)),
                    Q.where('phone', Q.like(`%${Q.sanitizeLikeString(searchTerm)}%`)),
                ),
                Q.sortBy('name', Q.asc),
            )
            .fetch();
    },

    async getCustomersWithCredit(businessId: string) {
        const customers = await customersCollection
            .query(Q.where('business_id', businessId), Q.where('deleted', false))
            .fetch();

        return customers.filter((c) => (c.currentBalance || 0) > 0);
    },

    async getCustomerWithSales(customerId: string) {
        const customer = await customersCollection.find(customerId);
        const sales = await customer.sales.fetch();
        return { customer, sales };
    },
};

hooks/useInventoryData.ts.content:
import { useBusiness } from '@/contexts/BusinessContext';
import { inventoryCollection } from '@/db';
import { categoryService } from '@/db/services/categoryService';
import { CategoryItem, InventoryViewItem } from '@/types';
import { Q } from '@nozbe/watermelondb';
import { useEffect, useMemo, useState } from 'react';

export function useInventoryData(): {
    inventory: InventoryViewItem[];
    categories: CategoryItem[];
    rawCategories: CategoryItem[];
    loading: boolean;
    refreshinvetoryData: () => Promise<void>;
} {
    const { selectedStore, selectedBusiness } = useBusiness();
    const [inventory, setInventory] = useState<InventoryViewItem[]>([]);
    const [rawCategories, setRawCategories] = useState<CategoryItem[]>([]);
    const [loading, setLoading] = useState(true);

    // Subscribe to inventory changes
    useEffect(() => {
        if (!selectedStore) {
            setLoading(false);
            return;
        }

        const subscription = inventoryCollection
            .query(Q.where('store_id', selectedStore.id))
            .observeWithColumns(['quantity', 'price', 'min_stock', 'max_stock', 'location'])
            .subscribe(async (invData) => {
                try {
                    const inventoryWithDetails = await Promise.all(
                        invData.map(async (inv: any) => {
                            const product = await inv.product.fetch();
                            const category = await product.category.fetch();

                            const viewItem: InventoryViewItem = {
                                id: inv.id,
                                productId: product.id,
                                name: product.name,
                                category: category.name,
                                categoryId: category.id,
                                categoryIcon: category.icon,
                                categoryColor: category.color,
                                quantity: inv.quantity || 0,
                                minStock: inv.minStock || 0,
                                maxStock: inv.maxStock || 100,
                                price: inv.price || 0,
                                cost: inv.weightedAvgCost || product.cost || 0,
                                unit: product.unit || 'pcs',
                                barcode: product.barcode || '',
                                location: inv.location || '',
                                lastUpdated: inv.lastUpdated || new Date(),
                            };

                            return viewItem;
                        }),
                    );

                    setInventory(inventoryWithDetails);
                    setLoading(false);
                } catch (error) {
                    console.error('Error processing inventory data:', error);
                    setLoading(false);
                }
            });

        return () => subscription.unsubscribe();
    }, [selectedStore]);

    // Subscribe to category changes
    useEffect(() => {
        if (!selectedBusiness) {
            // clear categories if no business selected
            setRawCategories([]);
            return;
        }
        const businessId = selectedBusiness.id;
        // Capture the id to ensure it's non-null within async callbacks

        if (!businessId) {
            return;
        }

        let cancelled = false;
        let subscription: any | undefined;

        async function startSubscription() {
            try {
                subscription = await categoryService.observeCategoriesByBusiness(
                    businessId,
                    (catData: any[]) => {
                        if (cancelled) return;
                        const mapped: CategoryItem[] = catData.map((cat: any) => ({
                            id: cat.id,
                            name: cat.name,
                            icon: cat.icon,
                            color: cat.color,
                            count: 0,
                        }));

                        setRawCategories(mapped);
                    },
                );
            } catch (err) {
                // handle or log error if observe fails
                console.error('Failed to observe categories:', err);
            }
        }

        startSubscription();

        return () => {
            cancelled = true;
            if (subscription?.unsubscribe) {
                subscription.unsubscribe();
            }
        };
    }, [selectedBusiness, selectedBusiness?.id]);

    // Calculate categories with counts based on current inventory
    const categories = useMemo(() => {
        const categoriesWithCounts: CategoryItem[] = rawCategories.map((cat) => ({
            ...cat,
            count: inventory.filter((item) => item.categoryId === cat.id).length,
        }));

        const allCategory: CategoryItem = {
            id: 'all',
            name: 'All',
            icon: '',
            color: 'bg-gray-600',
            count: inventory.length,
        };

        return [allCategory, ...categoriesWithCounts];
    }, [rawCategories, inventory]);

    const refreshinvetoryData = async () => {
        if (!selectedStore) {
            return;
        }

        setLoading(true);

        try {
            const invData = await inventoryCollection
                .query(Q.where('store_id', selectedStore.id))
                .fetch();

            const inventoryWithDetails = await Promise.all(
                invData.map(async (inv: any) => {
                    const product = await inv.product.fetch();
                    const category = await product.category.fetch();

                    const viewItem: InventoryViewItem = {
                        id: inv.id,
                        productId: product.id,
                        name: product.name,
                        category: category.name,
                        categoryId: category.id,
                        categoryIcon: category.icon,
                        categoryColor: category.color,
                        quantity: inv.quantity || 0,
                        minStock: inv.minStock || 0,
                        maxStock: inv.maxStock || 100,
                        price: inv.price || 0,
                        cost: inv.weightedAvgCost || product.cost || 0,
                        unit: product.unit || 'pcs',
                        barcode: product.barcode || '',
                        location: inv.location || '',
                        lastUpdated: inv.lastUpdated || new Date(),
                    };

                    return viewItem;
                }),
            );

            setInventory(inventoryWithDetails);
        } catch (error) {
            console.error('Error refreshing inventory data:', error);
        } finally {
            setLoading(false);
        }
    };

    return {
        inventory,
        categories,
        rawCategories,
        loading,
        refreshinvetoryData,
    };
}

app/(tabs)/checkout/complete-sale.tsx.content:
import React, { useState, useEffect } from 'react';
import {
    View,
    Text,
    TouchableOpacity,
    ScrollView,
    TextInput,
    Alert,
    ActivityIndicator,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { router, useLocalSearchParams } from 'expo-router';
import { useBusiness } from '@/contexts/BusinessContext';
import { useAuth } from '@/contexts/AuthContext';
import { useCart } from '@/hooks/useCart';
import { salesService } from '@/db/services/salesService';
import { customerService } from '@/db/services/customerService';

export default function CompleteSaleScreen() {
    const { selectedStore, selectedBusiness } = useBusiness();
    const { user } = useAuth();
    const { globalCart, totalCartPrice, clearCart } = useCart();
    const [loading, setLoading] = useState(false);
    const [customers, setCustomers] = useState<any[]>([]);
    const [selectedCustomer, setSelectedCustomer] = useState<any>(null);
    const [paymentMethod, setPaymentMethod] = useState<'cash' | 'card' | 'mobile'>('cash');
    const [onCredit, setOnCredit] = useState(false);
    const [discount, setDiscount] = useState('0');
    const [searchCustomer, setSearchCustomer] = useState('');

    const subtotal = totalCartPrice;
    const discountAmount = parseFloat(discount) || 0;
    const totalAmount = Math.max(0, subtotal - discountAmount);

    useEffect(() => {
        loadCustomers();
    }, [selectedBusiness]);

    const loadCustomers = async () => {
        if (!selectedBusiness) return;

        try {
            const custs = await customerService.getCustomersByBusiness(selectedBusiness.id);
            setCustomers(custs);
        } catch (error) {
            console.error('Error loading customers:', error);
        }
    };

    const handleCompleteSale = async () => {
        if (globalCart.length === 0) {
            Alert.alert('Error', 'Cart is empty');
            return;
        }

        if (onCredit && !selectedCustomer) {
            Alert.alert('Error', 'Please select a customer for credit sale');
            return;
        }

        if (onCredit && selectedCustomer) {
            const availableCredit = (selectedCustomer.creditLimit || 0) - (selectedCustomer.currentBalance || 0);
            if (totalAmount > availableCredit) {
                Alert.alert(
                    'Credit Limit Exceeded',
                    `Customer has ${availableCredit.toFixed(2)} available credit. Sale amount is ${totalAmount.toFixed(2)}`,
                    [{ text: 'OK' }]
                );
                return;
            }
        }

        if (!selectedStore || !user) {
            Alert.alert('Error', 'Missing store or user information');
            return;
        }

        setLoading(true);

        try {
            const saleItems = globalCart.map(item => ({
                productId: item.id,
                quantity: item.quantity,
                unitPrice: item.price,
                totalPrice: item.price * item.quantity,
            }));

            const sale = await salesService.createSale(
                {
                    storeId: selectedStore.id,
                    userId: user.id,
                    customerId: selectedCustomer?.id,
                    subtotal,
                    discountAmount,
                    discountPercentage: subtotal > 0 ? (discountAmount / subtotal) * 100 : 0,
                    totalAmount,
                    paymentMethod,
                    onCredit,
                },
                saleItems
            );

            await salesService.completeSale(sale.id, user.id);

            clearCart();

            Alert.alert(
                'Success',
                `Sale completed successfully!\nTotal: $${totalAmount.toFixed(2)}`,
                [
                    {
                        text: 'OK',
                        onPress: () => router.replace('/(tabs)/checkout'),
                    },
                ]
            );
        } catch (error) {
            console.error('Error completing sale:', error);
            Alert.alert('Error', 'Failed to complete sale');
        } finally {
            setLoading(false);
        }
    };

    const filteredCustomers = searchCustomer
        ? customers.filter(c => 
            c.name.toLowerCase().includes(searchCustomer.toLowerCase()) ||
            (c.phone && c.phone.includes(searchCustomer))
          )
        : customers;

    return (
        <SafeAreaView className="flex-1 bg-white">
            <View className="px-4 py-3 border-b border-gray-200">
                <View className="flex-row items-center justify-between">
                    <TouchableOpacity onPress={() => router.back()}>
                        <Text className="text-blue-600 text-base">Cancel</Text>
                    </TouchableOpacity>
                    <Text className="text-lg font-semibold">Complete Sale</Text>
                    <TouchableOpacity onPress={handleCompleteSale} disabled={loading}>
                        {loading ? (
                            <ActivityIndicator size="small" color="#3B82F6" />
                        ) : (
                            <Text className="text-blue-600 text-base font-semibold">Complete</Text>
                        )}
                    </TouchableOpacity>
                </View>
            </View>

            <ScrollView className="flex-1 px-4 py-4">
                <View className="mb-6">
                    <Text className="text-2xl font-bold mb-2">Total: ${totalAmount.toFixed(2)}</Text>
                    <Text className="text-sm text-gray-600">Subtotal: ${subtotal.toFixed(2)}</Text>
                    {discountAmount > 0 && (
                        <Text className="text-sm text-gray-600">Discount: -${discountAmount.toFixed(2)}</Text>
                    )}
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-2">Discount Amount</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={discount}
                        onChangeText={setDiscount}
                        placeholder="0.00"
                        keyboardType="decimal-pad"
                    />
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-2">Payment Method</Text>
                    <View className="flex-row gap-2">
                        {['cash', 'card', 'mobile'].map((method) => (
                            <TouchableOpacity
                                key={method}
                                className={`flex-1 py-3 rounded-lg ${
                                    paymentMethod === method ? 'bg-blue-600' : 'bg-gray-200'
                                }`}
                                onPress={() => setPaymentMethod(method as any)}
                            >
                                <Text className={`text-center font-semibold capitalize ${
                                    paymentMethod === method ? 'text-white' : 'text-gray-700'
                                }`}>
                                    {method}
                                </Text>
                            </TouchableOpacity>
                        ))}
                    </View>
                </View>

                <View className="mb-4">
                    <TouchableOpacity
                        className="flex-row items-center py-3"
                        onPress={() => setOnCredit(!onCredit)}
                    >
                        <View className={`w-6 h-6 rounded border-2 mr-3 items-center justify-center ${
                            onCredit ? 'bg-blue-600 border-blue-600' : 'border-gray-300'
                        }`}>
                            {onCredit && <Text className="text-white font-bold"></Text>}
                        </View>
                        <Text className="text-base">Sell on Credit</Text>
                    </TouchableOpacity>
                </View>

                {onCredit && (
                    <View className="mb-6">
                        <Text className="text-sm font-medium text-gray-700 mb-2">Select Customer</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                            value={searchCustomer}
                            onChangeText={setSearchCustomer}
                            placeholder="Search customers..."
                        />
                        <ScrollView className="max-h-48 border border-gray-300 rounded-lg">
                            {filteredCustomers.map((customer) => (
                                <TouchableOpacity
                                    key={customer.id}
                                    className={`p-3 border-b border-gray-200 ${
                                        selectedCustomer?.id === customer.id ? 'bg-blue-50' : ''
                                    }`}
                                    onPress={() => setSelectedCustomer(customer)}
                                >
                                    <Text className="font-medium">{customer.name}</Text>
                                    <Text className="text-sm text-gray-600">
                                        Credit: ${((customer.creditLimit || 0) - (customer.currentBalance || 0)).toFixed(2)} available
                                    </Text>
                                </TouchableOpacity>
                            ))}
                        </ScrollView>
                    </View>
                )}

                <View className="mb-6">
                    <Text className="text-base font-semibold mb-2">Cart Items ({globalCart.length})</Text>
                    {globalCart.map((item) => (
                        <View key={item.id} className="flex-row justify-between py-2 border-b border-gray-200">
                            <Text className="flex-1">{item.name}</Text>
                            <Text>{item.quantity}  ${item.price.toFixed(2)}</Text>
                            <Text className="ml-3 font-semibold">${(item.quantity * item.price).toFixed(2)}</Text>
                        </View>
                    ))}
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}

app/(tabs)/inventory/add-product.tsx.content:
import React, { useState, useEffect } from 'react';
import {
    View,
    Text,
    TextInput,
    TouchableOpacity,
    ScrollView,
    ActivityIndicator,
    Alert,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { router } from 'expo-router';
import { useBusiness } from '@/contexts/BusinessContext';
import { useAuth } from '@/contexts/AuthContext';
import { productService } from '@/db/services/productService';
import { categoryService } from '@/db/services/categoryService';
import { inventoryService } from '@/db/services/inventoryService';
import { database } from '@/db/index.web';

export default function AddProductScreen() {
    const { selectedBusiness, selectedStore } = useBusiness();
    const { user } = useAuth();
    const [loading, setLoading] = useState(false);
    const [categories, setCategories] = useState<any[]>([]);

    const [formData, setFormData] = useState({
        name: '',
        categoryId: '',
        cost: '',
        price: '',
        barcode: '',
        description: '',
        unit: 'pcs',
        initialQuantity: '',
        minStock: '',
        maxStock: '',
        location: '',
    });

    useEffect(() => {
        loadCategories();
    }, []);

    const loadCategories = async () => {
        if (!selectedBusiness) return;

        try {
            const cats = await categoryService.getCategoriesByBusiness(selectedBusiness.id);
            setCategories(cats);

            if (cats.length > 0 && !formData.categoryId) {
                setFormData(prev => ({ ...prev, categoryId: cats[0].id }));
            }
        } catch (error) {
            console.error('Error loading categories:', error);
        }
    };

    const handleSubmit = async () => {
        if (!formData.name.trim()) {
            Alert.alert('Error', 'Product name is required');
            return;
        }

        if (!formData.categoryId) {
            Alert.alert('Error', 'Please select a category');
            return;
        }

        if (!formData.price || parseFloat(formData.price) <= 0) {
            Alert.alert('Error', 'Please enter a valid price');
            return;
        }

        if (!selectedBusiness || !selectedStore || !user) {
            Alert.alert('Error', 'Missing business or store information');
            return;
        }

        setLoading(true);

        try {
            const product = await productService.createProduct({
                businessId: selectedBusiness.id,
                categoryId: formData.categoryId,
                name: formData.name,
                cost: parseFloat(formData.cost) || 0,
                barcode: formData.barcode,
                description: formData.description,
                unit: formData.unit || 'pcs',
                status: 'active',
            });

            const inventory = await inventoryService.createInventory({
                productId: product.id,
                storeId: selectedStore.id,
                quantity: 0,
                minStock: parseFloat(formData.minStock) || 0,
                maxStock: parseFloat(formData.maxStock) || 100,
                price: parseFloat(formData.price),
                wholeSalePrice: 0,
                weightedAvgCost: parseFloat(formData.cost) || 0,
                lastPurchasePrice: parseFloat(formData.cost) || 0,
                location: formData.location,
            });

            if (formData.initialQuantity && parseFloat(formData.initialQuantity) > 0) {
                await inventoryService.adjustInventoryWithBatch(
                    inventory.id,
                    parseFloat(formData.initialQuantity),
                    {
                        userId: user.id,
                        costPerUnit: parseFloat(formData.cost) || 0,
                        batchType: 'purchase',
                        notes: 'Initial stock',
                    }
                );
            }

            Alert.alert('Success', 'Product added successfully!', [
                {
                    text: 'OK',
                    onPress: () => router.back(),
                },
            ]);
        } catch (error) {
            console.error('Error creating product:', error);
            Alert.alert('Error', 'Failed to create product');
        } finally {
            setLoading(false);
        }
    };

    return (
        <SafeAreaView className="flex-1 bg-white">
            <View className="px-4 py-3 border-b border-gray-200">
                <View className="flex-row items-center justify-between">
                    <TouchableOpacity onPress={() => router.back()}>
                        <Text className="text-blue-600 text-base">Cancel</Text>
                    </TouchableOpacity>
                    <Text className="text-lg font-semibold">Add Product</Text>
                    <TouchableOpacity
                        onPress={handleSubmit}
                        disabled={loading}
                    >
                        {loading ? (
                            <ActivityIndicator size="small" color="#3B82F6" />
                        ) : (
                            <Text className="text-blue-600 text-base font-semibold">Save</Text>
                        )}
                    </TouchableOpacity>
                </View>
            </View>

            <ScrollView className="flex-1 px-4 py-4">
                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Product Name *</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={formData.name}
                        onChangeText={(text) => setFormData(prev => ({ ...prev, name: text }))}
                        placeholder="Enter product name"
                    />
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Category *</Text>
                    <View className="border border-gray-300 rounded-lg">
                        {categories.map((cat) => (
                            <TouchableOpacity
                                key={cat.id}
                                className={`px-3 py-3 border-b border-gray-200 ${
                                    formData.categoryId === cat.id ? 'bg-blue-50' : ''
                                }`}
                                onPress={() => setFormData(prev => ({ ...prev, categoryId: cat.id }))}
                            >
                                <Text className={formData.categoryId === cat.id ? 'text-blue-600 font-medium' : ''}>
                                    {cat.icon} {cat.name}
                                </Text>
                            </TouchableOpacity>
                        ))}
                    </View>
                </View>

                <View className="flex-row mb-4 gap-3">
                    <View className="flex-1">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Cost Price</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2"
                            value={formData.cost}
                            onChangeText={(text) => setFormData(prev => ({ ...prev, cost: text }))}
                            placeholder="0.00"
                            keyboardType="decimal-pad"
                        />
                    </View>
                    <View className="flex-1">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Selling Price *</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2"
                            value={formData.price}
                            onChangeText={(text) => setFormData(prev => ({ ...prev, price: text }))}
                            placeholder="0.00"
                            keyboardType="decimal-pad"
                        />
                    </View>
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Barcode/SKU</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={formData.barcode}
                        onChangeText={(text) => setFormData(prev => ({ ...prev, barcode: text }))}
                        placeholder="Enter barcode or SKU"
                    />
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Unit</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={formData.unit}
                        onChangeText={(text) => setFormData(prev => ({ ...prev, unit: text }))}
                        placeholder="pcs, kg, liter, etc."
                    />
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Initial Quantity</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={formData.initialQuantity}
                        onChangeText={(text) => setFormData(prev => ({ ...prev, initialQuantity: text }))}
                        placeholder="0"
                        keyboardType="numeric"
                    />
                </View>

                <View className="flex-row mb-4 gap-3">
                    <View className="flex-1">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Min Stock</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2"
                            value={formData.minStock}
                            onChangeText={(text) => setFormData(prev => ({ ...prev, minStock: text }))}
                            placeholder="0"
                            keyboardType="numeric"
                        />
                    </View>
                    <View className="flex-1">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Max Stock</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2"
                            value={formData.maxStock}
                            onChangeText={(text) => setFormData(prev => ({ ...prev, maxStock: text }))}
                            placeholder="100"
                            keyboardType="numeric"
                        />
                    </View>
                </View>

                <View className="mb-4">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Location</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={formData.location}
                        onChangeText={(text) => setFormData(prev => ({ ...prev, location: text }))}
                        placeholder="Shelf, Aisle, etc."
                    />
                </View>

                <View className="mb-6">
                    <Text className="text-sm font-medium text-gray-700 mb-1">Description</Text>
                    <TextInput
                        className="border border-gray-300 rounded-lg px-3 py-2"
                        value={formData.description}
                        onChangeText={(text) => setFormData(prev => ({ ...prev, description: text }))}
                        placeholder="Product description (optional)"
                        multiline
                        numberOfLines={3}
                        textAlignVertical="top"
                    />
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}

hooks/useCheckoutData.ts.content:
import { useState, useEffect } from 'react';
import { useBusiness } from '@/contexts/BusinessContext';
import { inventoryService } from '@/db/services/inventoryService';
import { categoryService } from '@/db/services/categoryService';
import { customerService } from '@/db/services/customerService';

export function useCheckoutData() {
    const { selectedStore, selectedBusiness } = useBusiness();
    const [products, setProducts] = useState<any[]>([]);
    const [categories, setCategories] = useState<any[]>([]);
    const [customers, setCustomers] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        loadData();
    }, [selectedStore, selectedBusiness]);

    const loadData = async () => {
        if (!selectedStore || !selectedBusiness) {
            setLoading(false);
            return;
        }

        try {
            const [invData, catData, custData] = await Promise.all([
                inventoryService.getInventoryByStore(selectedStore.id),
                categoryService.getCategoriesByBusiness(selectedBusiness.id),
                customerService.getCustomersByBusiness(selectedBusiness.id),
            ]);

            const productsWithDetails = await Promise.all(
                invData
                    .filter(inv => (inv.quantity || 0) > 0) 
                    .map(async (inv) => {
                        const product = await inv.product.fetch();
                        const category = await product.category.fetch();

                        return {
                            id: product.id,
                            inventoryId: inv.id,
                            name: product.name,
                            category: category.name,
                            categoryId: category.id,
                            categoryIcon: category.icon,
                            categoryColor: category.color,
                            price: inv.price || 0,
                            quantity: inv.quantity || 0,
                            unit: product.unit || 'pcs',
                            barcode: product.barcode || '',
                        };
                    })
            );

            setProducts(productsWithDetails);
            setCategories(catData.map(cat => ({
                id: cat.id,
                name: cat.name,
                icon: cat.icon,
                color: cat.color,
            })));
            setCustomers(custData);
        } catch (error) {
            console.error('Error loading checkout data:', error);
        } finally {
            setLoading(false);
        }
    };

    const refreshData = () => {
        setLoading(true);
        loadData();
    };

    return {
        products,
        categories,
        customers,
        loading,
        refreshData,
    };
}

app/(tabs)/inventory/manage-categories.t.content:
import React, { useState, useEffect } from 'react';
import {
    View,
    Text,
    TextInput,
    TouchableOpacity,
    ScrollView,
    Alert,
    ActivityIndicator,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { router } from 'expo-router';
import { useBusiness } from '@/contexts/BusinessContext';
import { categoryService } from '@/db/services/categoryService';

export default function ManageCategoriesScreen() {
    const { selectedBusiness } = useBusiness();
    const [categories, setCategories] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [showAddForm, setShowAddForm] = useState(false);
    const [editingId, setEditingId] = useState<string | null>(null);
    const [formData, setFormData] = useState({
        name: '',
        icon: '',
        color: '#6B7280',
    });

    const icons = ['', '', '', '', '', '', '', '', '', ''];
    const colors = ['#EF4444', '#F59E0B', '#10B981', '#3B82F6', '#8B5CF6', '#EC4899', '#6B7280'];

    useEffect(() => {
        loadCategories();
    }, []);

    const loadCategories = async () => {
        if (!selectedBusiness) return;

        try {
            const cats = await categoryService.getCategoriesByBusiness(selectedBusiness.id);
            setCategories(cats);
        } catch (error) {
            console.error('Error loading categories:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async () => {
        if (!formData.name.trim()) {
            Alert.alert('Error', 'Category name is required');
            return;
        }

        if (!selectedBusiness) return;

        try {
            if (editingId) {
                await categoryService.updateCategory(editingId, formData);
                Alert.alert('Success', 'Category updated successfully');
            } else {
                await categoryService.createCategory({
                    businessId: selectedBusiness.id,
                    ...formData,
                });
                Alert.alert('Success', 'Category created successfully');
            }

            setFormData({ name: '', icon: '', color: '#6B7280' });
            setShowAddForm(false);
            setEditingId(null);
            loadCategories();
        } catch (error) {
            console.error('Error saving category:', error);
            Alert.alert('Error', 'Failed to save category');
        }
    };

    const handleEdit = (category: any) => {
        setFormData({
            name: category.name,
            icon: category.icon,
            color: category.color,
        });
        setEditingId(category.id);
        setShowAddForm(true);
    };

    const handleDelete = (category: any) => {
        Alert.alert(
            'Delete Category',
            `Are you sure you want to delete "${category.name}"?`,
            [
                { text: 'Cancel', style: 'cancel' },
                {
                    text: 'Delete',
                    style: 'destructive',
                    onPress: async () => {
                        try {
                            await categoryService.deleteCategory(category.id);
                            loadCategories();
                        } catch (error) {
                            Alert.alert('Error', 'Failed to delete category');
                        }
                    },
                },
            ]
        );
    };

    if (loading) {
        return (
            <SafeAreaView className="flex-1 bg-white items-center justify-center">
                <ActivityIndicator size="large" color="#3B82F6" />
            </SafeAreaView>
        );
    }

    return (
        <SafeAreaView className="flex-1 bg-white">
            <View className="px-4 py-3 border-b border-gray-200">
                <View className="flex-row items-center justify-between">
                    <TouchableOpacity onPress={() => router.back()}>
                        <Text className="text-blue-600 text-base">Back</Text>
                    </TouchableOpacity>
                    <Text className="text-lg font-semibold">Manage Categories</Text>
                    <TouchableOpacity
                        onPress={() => {
                            setShowAddForm(!showAddForm);
                            setEditingId(null);
                            setFormData({ name: '', icon: '', color: '#6B7280' });
                        }}
                    >
                        <Text className="text-blue-600 text-2xl">{showAddForm ? '' : '+'}</Text>
                    </TouchableOpacity>
                </View>
            </View>

            <ScrollView className="flex-1 px-4 py-4">
                {showAddForm && (
                    <View className="mb-6 p-4 bg-gray-50 rounded-lg">
                        <Text className="text-base font-semibold mb-3">
                            {editingId ? 'Edit Category' : 'New Category'}
                        </Text>

                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-2 mb-3"
                            value={formData.name}
                            onChangeText={(text) => setFormData(prev => ({ ...prev, name: text }))}
                            placeholder="Category name"
                        />

                        <Text className="text-sm font-medium text-gray-700 mb-2">Select Icon</Text>
                        <View className="flex-row flex-wrap gap-2 mb-3">
                            {icons.map((icon) => (
                                <TouchableOpacity
                                    key={icon}
                                    className={`w-12 h-12 items-center justify-center rounded-lg ${
                                        formData.icon === icon ? 'bg-blue-100' : 'bg-gray-200'
                                    }`}
                                    onPress={() => setFormData(prev => ({ ...prev, icon }))}
                                >
                                    <Text className="text-2xl">{icon}</Text>
                                </TouchableOpacity>
                            ))}
                        </View>

                        <Text className="text-sm font-medium text-gray-700 mb-2">Select Color</Text>
                        <View className="flex-row flex-wrap gap-2 mb-4">
                            {colors.map((color) => (
                                <TouchableOpacity
                                    key={color}
                                    className="w-12 h-12 rounded-lg"
                                    style={{
                                        backgroundColor: color,
                                        borderWidth: formData.color === color ? 3 : 0,
                                        borderColor: '#000',
                                    }}
                                    onPress={() => setFormData(prev => ({ ...prev, color }))}
                                />
                            ))}
                        </View>

                        <TouchableOpacity
                            className="bg-blue-600 rounded-lg py-3 items-center"
                            onPress={handleSubmit}
                        >
                            <Text className="text-white font-semibold">
                                {editingId ? 'Update' : 'Create'} Category
                            </Text>
                        </TouchableOpacity>
                    </View>
                )}

                <View>
                    {categories.map((category) => (
                        <View
                            key={category.id}
                            className="flex-row items-center justify-between py-3 border-b border-gray-200"
                        >
                            <View className="flex-row items-center flex-1">
                                <View
                                    className="w-10 h-10 rounded-lg items-center justify-center mr-3"
                                    style={{ backgroundColor: category.color }}
                                >
                                    <Text className="text-xl">{category.icon}</Text>
                                </View>
                                <Text className="text-base font-medium">{category.name}</Text>
                            </View>
                            <View className="flex-row gap-3">
                                <TouchableOpacity onPress={() => handleEdit(category)}>
                                    <Text className="text-blue-600">Edit</Text>
                                </TouchableOpacity>
                                <TouchableOpacity onPress={() => handleDelete(category)}>
                                    <Text className="text-red-600">Delete</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    ))}
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}

components/inventory/AddProductModal.tsx.content:
import React, { useState, useEffect } from 'react';
import {
    View,
    Text,
    TextInput,
    TouchableOpacity,
    ScrollView,
    ActivityIndicator,
    Alert,
    Modal,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Ionicons } from '@expo/vector-icons';
import { useBusiness } from '@/contexts/BusinessContext';
import { useAuth } from '@/contexts/AuthContext';
import { productService } from '@/db/services/productService';
import { categoryService } from '@/db/services/categoryService';
import { inventoryService } from '@/db/services/inventoryService';

interface AddProductModalProps {
    visible: boolean;
    onClose: () => void;
    onProductAdded?: () => void;
}

export const AddProductModal: React.FC<AddProductModalProps> = ({
    visible,
    onClose,
    onProductAdded,
}) => {
    const { selectedBusiness, selectedStore } = useBusiness();
    const { user } = useAuth();
    const [loading, setLoading] = useState(false);
    const [categories, setCategories] = useState<any[]>([]);
    const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);

    const [formData, setFormData] = useState({
        name: '',
        categoryId: '',
        cost: '',
        price: '',
        barcode: '',
        description: '',
        unit: 'pcs',
        initialQuantity: '',
        minStock: '',
        maxStock: '',
        location: '',
    });

    useEffect(() => {
        if (visible) {
            loadCategories();
        }
    }, [visible]);

    const loadCategories = async () => {
        if (!selectedBusiness) return;

        try {
            const cats = await categoryService.getCategoriesByBusiness(selectedBusiness.id);
            setCategories(cats);

            if (cats.length > 0 && !formData.categoryId) {
                setFormData((prev) => ({ ...prev, categoryId: cats[0].id }));
            }
        } catch (error) {
            console.error('Error loading categories:', error);
        }
    };

    const handleSubmit = async () => {
        if (!formData.name.trim()) {
            Alert.alert('Error', 'Product name is required');
            return;
        }

        if (!formData.categoryId) {
            Alert.alert('Error', 'Please select a category');
            return;
        }

        if (!formData.price || parseFloat(formData.price) <= 0) {
            Alert.alert('Error', 'Please enter a valid price');
            return;
        }

        if (!selectedBusiness || !selectedStore || !user) {
            Alert.alert('Error', 'Missing business or store information');
            return;
        }

        setLoading(true);

        try {
            const product = await productService.createProduct({
                businessId: selectedBusiness.id,
                categoryId: formData.categoryId,
                name: formData.name,
                cost: parseFloat(formData.cost) || 0,
                barcode: formData.barcode,
                description: formData.description,
                unit: formData.unit || 'pcs',
                status: 'active',
            });

            const inventory = await inventoryService.createInventory({
                productId: product.id,
                storeId: selectedStore.id,
                quantity: 0,
                minStock: parseFloat(formData.minStock) || 0,
                maxStock: parseFloat(formData.maxStock) || 100,
                price: parseFloat(formData.price),
                wholeSalePrice: 0,
                weightedAvgCost: parseFloat(formData.cost) || 0,
                lastPurchasePrice: parseFloat(formData.cost) || 0,
                location: formData.location,
            });

            if (formData.initialQuantity && parseFloat(formData.initialQuantity) > 0) {
                await inventoryService.adjustInventoryWithBatch(
                    inventory.id,
                    parseFloat(formData.initialQuantity),
                    {
                        userId: user.id,
                        costPerUnit: parseFloat(formData.cost) || 0,
                        batchType: 'purchase',
                        notes: 'Initial stock',
                    }
                );
            }

            Alert.alert('Success', 'Product added successfully!');
            resetForm();
            onProductAdded?.();
            onClose();
        } catch (error) {
            console.error('Error creating product:', error);
            Alert.alert('Error', 'Failed to create product');
        } finally {
            setLoading(false);
        }
    };

    const resetForm = () => {
        setFormData({
            name: '',
            categoryId: categories.length > 0 ? categories[0].id : '',
            cost: '',
            price: '',
            barcode: '',
            description: '',
            unit: 'pcs',
            initialQuantity: '',
            minStock: '',
            maxStock: '',
            location: '',
        });
    };

    const handleClose = () => {
        resetForm();
        onClose();
    };

    const selectedCategory = categories.find((cat) => cat.id === formData.categoryId);

    return (
        <Modal
            visible={visible}
            animationType="slide"
            presentationStyle="pageSheet"
            onRequestClose={handleClose}
        >
            <SafeAreaView className="flex-1 bg-white">
                <View className="flex-row justify-between items-center px-4 py-4 border-b border-gray-200">
                    <Text className="text-xl font-bold text-black">Add New Product</Text>
                    <TouchableOpacity onPress={handleClose}>
                        <Ionicons name="close" size={24} color="#666" />
                    </TouchableOpacity>
                </View>

                <ScrollView className="flex-1 px-4 py-6" showsVerticalScrollIndicator={false}>
                    <Text className="text-gray-600 text-center mb-6">
                        Create a new product in your inventory with all necessary details.
                    </Text>

                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">
                            Product Name <Text className="text-red-500">*</Text>
                        </Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-3"
                            value={formData.name}
                            onChangeText={(text) => setFormData((prev) => ({ ...prev, name: text }))}
                            placeholder="Enter product name"
                        />
                    </View>

                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">
                            Category <Text className="text-red-500">*</Text>
                        </Text>
                        <TouchableOpacity
                            className="border border-gray-300 rounded-lg px-3 py-3 flex-row justify-between items-center"
                            onPress={() => setShowCategoryDropdown(!showCategoryDropdown)}
                        >
                            <Text className={selectedCategory ? 'text-gray-900' : 'text-gray-400'}>
                                {selectedCategory ? `${selectedCategory.icon} ${selectedCategory.name}` : 'Select category'}
                            </Text>
                            <Ionicons
                                name={showCategoryDropdown ? 'chevron-up' : 'chevron-down'}
                                size={20}
                                color="#666"
                            />
                        </TouchableOpacity>

                        {showCategoryDropdown && (
                            <View className="border border-gray-300 rounded-lg mt-2 overflow-hidden">
                                <ScrollView style={{ maxHeight: 200 }}>
                                    {categories.map((cat) => (
                                        <TouchableOpacity
                                            key={cat.id}
                                            className={`px-3 py-3 border-b border-gray-200 ${
                                                formData.categoryId === cat.id ? 'bg-blue-50' : ''
                                            }`}
                                            onPress={() => {
                                                setFormData((prev) => ({ ...prev, categoryId: cat.id }));
                                                setShowCategoryDropdown(false);
                                            }}
                                        >
                                            <Text
                                                className={
                                                    formData.categoryId === cat.id
                                                        ? 'text-blue-600 font-medium'
                                                        : 'text-gray-900'
                                                }
                                            >
                                                {cat.icon} {cat.name}
                                            </Text>
                                        </TouchableOpacity>
                                    ))}
                                </ScrollView>
                            </View>
                        )}
                    </View>

                    <View className="flex-row mb-4 gap-3">
                        <View className="flex-1">
                            <Text className="text-sm font-medium text-gray-700 mb-1">Cost Price</Text>
                            <TextInput
                                className="border border-gray-300 rounded-lg px-3 py-3"
                                value={formData.cost}
                                onChangeText={(text) => setFormData((prev) => ({ ...prev, cost: text }))}
                                placeholder="0.00"
                                keyboardType="decimal-pad"
                            />
                        </View>
                        <View className="flex-1">
                            <Text className="text-sm font-medium text-gray-700 mb-1">
                                Selling Price <Text className="text-red-500">*</Text>
                            </Text>
                            <TextInput
                                className="border border-gray-300 rounded-lg px-3 py-3"
                                value={formData.price}
                                onChangeText={(text) => setFormData((prev) => ({ ...prev, price: text }))}
                                placeholder="0.00"
                                keyboardType="decimal-pad"
                            />
                        </View>
                    </View>

                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Barcode/SKU</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-3"
                            value={formData.barcode}
                            onChangeText={(text) => setFormData((prev) => ({ ...prev, barcode: text }))}
                            placeholder="Enter barcode or SKU"
                        />
                    </View>

                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Unit</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-3"
                            value={formData.unit}
                            onChangeText={(text) => setFormData((prev) => ({ ...prev, unit: text }))}
                            placeholder="pcs, kg, liter, etc."
                        />
                    </View>

                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Initial Quantity</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-3"
                            value={formData.initialQuantity}
                            onChangeText={(text) =>
                                setFormData((prev) => ({ ...prev, initialQuantity: text }))
                            }
                            placeholder="0"
                            keyboardType="numeric"
                        />
                    </View>

                    <View className="flex-row mb-4 gap-3">
                        <View className="flex-1">
                            <Text className="text-sm font-medium text-gray-700 mb-1">Min Stock</Text>
                            <TextInput
                                className="border border-gray-300 rounded-lg px-3 py-3"
                                value={formData.minStock}
                                onChangeText={(text) =>
                                    setFormData((prev) => ({ ...prev, minStock: text }))
                                }
                                placeholder="0"
                                keyboardType="numeric"
                            />
                        </View>
                        <View className="flex-1">
                            <Text className="text-sm font-medium text-gray-700 mb-1">Max Stock</Text>
                            <TextInput
                                className="border border-gray-300 rounded-lg px-3 py-3"
                                value={formData.maxStock}
                                onChangeText={(text) =>
                                    setFormData((prev) => ({ ...prev, maxStock: text }))
                                }
                                placeholder="100"
                                keyboardType="numeric"
                            />
                        </View>
                    </View>

                    <View className="mb-4">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Location</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-3"
                            value={formData.location}
                            onChangeText={(text) => setFormData((prev) => ({ ...prev, location: text }))}
                            placeholder="Shelf, Aisle, etc."
                        />
                    </View>

                    <View className="mb-6">
                        <Text className="text-sm font-medium text-gray-700 mb-1">Description</Text>
                        <TextInput
                            className="border border-gray-300 rounded-lg px-3 py-3"
                            value={formData.description}
                            onChangeText={(text) =>
                                setFormData((prev) => ({ ...prev, description: text }))
                            }
                            placeholder="Product description (optional)"
                            multiline
                            numberOfLines={3}
                            textAlignVertical="top"
                        />
                    </View>
                </ScrollView>

                <View className="flex-row px-4 py-4 space-x-3 border-t border-gray-200">
                    <TouchableOpacity
                        className="flex-1 bg-gray-200 rounded-xl py-4"
                        onPress={handleClose}
                        disabled={loading}
                    >
                        <Text className="text-center font-semibold text-gray-700">Cancel</Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        className={`flex-1 rounded-xl py-4 ${
                            formData.name && formData.categoryId && formData.price
                                ? 'bg-black'
                                : 'bg-gray-300'
                        }`}
                        onPress={handleSubmit}
                        disabled={
                            !formData.name || !formData.categoryId || !formData.price || loading
                        }
                    >
                        {loading ? (
                            <ActivityIndicator size="small" color="#FFFFFF" />
                        ) : (
                            <Text
                                className={`text-center font-semibold ${
                                    formData.name && formData.categoryId && formData.price
                                        ? 'text-white'
                                        : 'text-gray-500'
                                }`}
                            >
                                Add Product
                            </Text>
                        )}
                    </TouchableOpacity>
                </View>
            </SafeAreaView>
        </Modal>
    );
};

components/inventory/modal/productModal..content:
import { Category } from '@/db';
import { CategoryData, CategoryItem, InventoryItemData, ProductData } from '@/types';
import { Ionicons } from '@expo/vector-icons';
import React, { useState } from 'react';
import {
    ActivityIndicator,
    Alert,
    Modal,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    View,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import FormInput from '../FormInput';

interface AddProductModalProps {
    visible: boolean;
    onClose: () => void;
    onProductAdded?: () => void;
    categories: CategoryItem[];
    addCategory: (newCategoryData: CategoryData) => Promise<Category | undefined>;
    loadingCategories: boolean;
    refreshCategories: () => Promise<void>;
    addProduct: (
        newPoduct: ProductData,
        newInventoryProductData: InventoryItemData,
    ) => Promise<void>;
}

export const ProductModal: React.FC<AddProductModalProps> = ({
    visible,
    onClose,
    addCategory,
    loadingCategories,
    refreshCategories,
    onProductAdded,
    addProduct,
    categories,
}) => {
    const [loading, setLoading] = useState(false);
    const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
    const [showCreateCategory, setShowCreateCategory] = useState(false);
    const [newCategoryData, setNewCategoryData] = useState({
        name: '',
        icon: '',
        color: '#6B7280',
    });

    const [formData, setFormData] = useState({
        name: '',
        categoryId: '',
        cost: '',
        price: '',
        wholeSalePrice: '',
        barcode: '',
        description: '',
        unit: '',
        quantity: '',
        quantityPerUnit: '',
        minStock: '',
        maxStock: '',
        location: '',
    });

    const handleCreateCategory = async () => {
        try {
            const newCategory = await addCategory(newCategoryData);
            await refreshCategories();
            if (!newCategory) {
                Alert.alert('Error', 'no category created');
                return;
            }

            setFormData((prev) => ({ ...prev, categoryId: newCategory.id }));
            setShowCreateCategory(false);
            setShowCategoryDropdown(false);
            setNewCategoryData({ name: '', icon: '', color: '#6B7280' });
            Alert.alert('Success', 'Category created successfully!');
        } catch (error) {
            console.error('Error creating category:', error);
            Alert.alert('Error', 'Failed to create category');
        }
    };

    const showCreateCategoryForm = () => {
        console.log('Show categories', categories);

        if (categories.length > 0 && !formData.categoryId) {
            setFormData((prev) => ({ ...prev, categoryId: categories[0].id }));
        }
        refreshCategories();
        setShowCategoryDropdown(!showCategoryDropdown);
    };

    const handleSubmit = async () => {
        if (!formData.name.trim()) {
            Alert.alert('Error', 'Product name is required');
            return;
        }

        if (!formData.categoryId) {
            Alert.alert('Error', 'Please select a category');
            return;
        }

        if (!formData.price || parseFloat(formData.price) <= 0) {
            Alert.alert('Error', 'Please enter a valid price');
            return;
        }

        setLoading(true);

        try {
            const newPoduct: ProductData = {
                name: formData.name,
                categoryId: formData.categoryId,
                cost: formData.cost,
                barcode: formData.barcode,
                description: formData.description,
                unit: formData.unit || 'pcs',
                minStock: parseFloat(formData.minStock) || 0,
                maxStock: parseFloat(formData.maxStock) || 100,
                price: parseFloat(formData.price),
                location: formData.location,
                initialQuantity: parseFloat(formData.quantity) || 0,
            };

            const newInventoryProductData: InventoryItemData = {
                quantity: parseFloat(formData.quantity) || 0,
                price: parseFloat(formData.price),
                minStock: parseFloat(formData.minStock) || 0,
                maxStock: parseFloat(formData.maxStock) || 100,
                wholeSalePrice: 0,
                weightedAvgCost: parseFloat(formData.cost) || 0,
                lastPurchasePrice: parseFloat(formData.cost) || 0,
                location: formData.location,
            };
            await addProduct(newPoduct, newInventoryProductData);

            Alert.alert('Success', 'Product added successfully!');
            resetForm();
            onProductAdded?.();
            onClose();
        } catch (error) {
            console.error('Error creating product:', error);
            Alert.alert('Error', 'Failed to create product');
        } finally {
            setLoading(false);
        }
    };

    const resetForm = () => {
        setFormData({
            name: '',
            categoryId: categories.length > 0 ? categories[0].id : '',
            cost: '',
            price: '',
            wholeSalePrice: '',
            barcode: '',
            description: '',
            unit: 'pcs',
            quantity: '',
            quantityPerUnit: '',
            minStock: '',
            maxStock: '',
            location: '',
        });
    };

    const handleClose = () => {
        resetForm();
        onClose();
    };

    const selectedCategory = categories.find((cat) => cat.id === formData.categoryId);

    return (
        <Modal
            visible={visible}
            animationType='slide'
            presentationStyle='pageSheet'
            onRequestClose={handleClose}>
            <SafeAreaView className='flex-1 bg-white'>
                <View className='flex-row justify-between items-center px-4 py-4 border-b border-gray-200'>
                    <Text className='text-xl font-bold text-black'>Add New Product</Text>
                    <TouchableOpacity onPress={handleClose}>
                        <Ionicons name='close' size={24} color='#666' />
                    </TouchableOpacity>
                </View>

                <ScrollView className='flex-1 px-4 py-6' showsVerticalScrollIndicator={false}>
                    <Text className='text-gray-600 text-center mb-6'>
                        Create a new product in your inventory with all necessary details.
                    </Text>

                    <View className='mb-4'>
                        <FormInput
                            label='Product Name'
                            required={true}
                            value={formData.name}
                            onChangeText={(text) =>
                                setFormData((prev) => ({ ...prev, name: text }))
                            }
                            placeholder='Enter product name'
                        />
                    </View>

                    <View className='mb-4'>
                        <Text className='text-sm font-medium text-gray-700 mb-1'>
                            Category <Text className='text-red-500'>*</Text>
                        </Text>
                        <TouchableOpacity
                            className='bg-gray-100 rounded-lg  text-gray-700 text-baserounded-lg px-3 py-3 flex-row justify-between items-center'
                            // onPress={() => setShowCategoryDropdown(!showCategoryDropdown)}>
                            onPress={showCreateCategoryForm}>
                            <Text className={selectedCategory ? 'text-gray-900' : 'text-gray-400'}>
                                {selectedCategory
                                    ? `${selectedCategory.icon} ${selectedCategory.name}`
                                    : 'Select category'}
                            </Text>
                            <Ionicons
                                name={showCategoryDropdown ? 'chevron-up' : 'chevron-down'}
                                size={20}
                                color='#666'
                            />
                        </TouchableOpacity>

                        {showCategoryDropdown && (
                            <View className='border border-gray-300 rounded-lg mt-2 overflow-hidden'>
                                <ScrollView style={{ maxHeight: 200 }}>
                                    {categories.map((cat) => (
                                        <TouchableOpacity
                                            key={cat.id}
                                            className={`px-3 py-3 border-b border-gray-200 ${
                                                formData.categoryId === cat.id ? 'bg-blue-50' : ''
                                            }`}
                                            onPress={() => {
                                                setFormData((prev) => ({
                                                    ...prev,
                                                    categoryId: cat.id,
                                                }));
                                                setShowCategoryDropdown(false);
                                            }}>
                                            <Text
                                                className={
                                                    formData.categoryId === cat.id
                                                        ? 'text-blue-600 font-medium'
                                                        : 'text-gray-900'
                                                }>
                                                {cat.icon} {cat.name}
                                            </Text>
                                        </TouchableOpacity>
                                    ))}
                                    <TouchableOpacity
                                        className='px-3 py-3 bg-gray-50 border-t border-gray-300'
                                        onPress={() => {
